package com.ness.fw.flower.core;

import java.util.*;

/**
 * Internal event that is generated by the framework to cause {@link SimpleFlowState} processing. 
 * Does not have any attribute - even not a name.
 */
public class SimpleStateEvent extends Event
{
	/**
	 * create new SimpleStateEvent Object
	 */
	public SimpleStateEvent()
	{
		super(null, null, null);
	}

	/**
	 * Returns the parameter data from the event parameters according to the given key.
	 * not implemented.
	 *
	 * @param key The key of the parameter.
	 * @return String The parameter data.
	 */
	public String getParameter(String key)
	{
		return null;
	}

	/**
	 * Used to get event parameter values (passes call to http request)
	 */
	public String[] getParameterValues(String key)
	{
		return null;
	}

	/**
	 * Return {@link Enumeration} of all parameters names
	 * not implemented.
	 * 
	 * @return Enumeration
	 */
	public Enumeration getParameterNames()
	{
		return null;
	}

	/**
	 * Indicates whether the event contains parameters. 
	 * @return boolean
	 */
	public boolean isContainsParameters()
	{
		return false;
	}

}
